function deleteGACookies(){const e=document.cookie.split(";");e.forEach(e=>{const t=e.split("=")[0].trim();(t.startsWith("_ga")||t==="_gid"||t==="_gat")&&(document.cookie=t+"=; expires=Thu, 01 Jan 1970 00:00:00 GMT; path=/")})}document.addEventListener("DOMContentLoaded",function(){const n=document.getElementById("reset-cookie-options"),e=document.getElementById("cookie-spacer"),t=document.getElementById("cookie-banner"),s=document.getElementById("cookie-accept"),o=document.getElementById("cookie-decline");localStorage.getItem("cookieConsent")||(e.classList.remove("hidden"),t.classList.remove("hidden")),n.addEventListener("click",n=>{n.preventDefault(),localStorage.removeItem("cookieConsent"),deleteGACookies(),e.classList.remove("hidden"),t.classList.remove("hidden")}),s.addEventListener("click",()=>{localStorage.setItem("cookieConsent","accepted"),gtag("consent","update",{analytics_storage:"granted",ad_storage:"denied"}),e.classList.add("hidden"),t.classList.add("hidden")}),o.addEventListener("click",()=>{localStorage.setItem("cookieConsent","declined"),gtag("consent","update",{analytics_storage:"denied"}),e.classList.add("hidden"),t.classList.add("hidden"),deleteGACookies()})})